import React, { useState, useEffect } from 'react'
import { Tag } from 'antd'
import axios from 'axios'
import request from '../config/api'
import '../static/styles/components/tag.css'

const MyTag = () => {
  const [tagArray, setTagArray] = useState([])
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(request.fetchAllTagApi).then(res => {
        return res.data.data.tags
      })

      setTagArray(result)
    }

    fetchData()
  }, [])

  const handleClick = e => {
    console.log(e)
  }

  return (
    <div className="comm-box tag-container">
      {
        tagArray.map(item => (<Tag onClick={handleClick()} key={item.id} color={item.color}>{item.name}</Tag>))
      }
    </div>
  )
}

export default MyTag
